$(document).ready(function(){var a=new LoginValidator;new LoginController;$("#login-form").ajaxForm({beforeSubmit:function(b,c,d){return 0!=a.validateForm()&&(b.push({name:"remember-me",value:1==$("input:checkbox:checked").length}),!0)},success:function(a,b,c,d){"success"==b&&(window.location.href="/home")},error:function(b){console.log(b),a.showLoginError("Login Failure","Please check your username and/or password")}}),$("#user-tf").focus();var b=new EmailValidator;$("#get-credentials-form").ajaxForm({url:"/lost-password",beforeSubmit:function(a,c,d){return b.validateEmail($("#email-tf").val())?(b.hideEmailAlert(),!0):(b.showEmailAlert("<b> Error!</b> Please enter a valid email address"),!1)},success:function(a,c,d,e){b.showEmailSuccess("Check your email on how to reset your password.")},error:function(){b.showEmailAlert("Sorry. There was a problem, please try again later.")}})});